import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { ExclamationCircleIcon } from '@heroicons/react/24/outline';

function App() {
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([
    { role: 'assistant', content: 'Hello! How can I help you with your studies today?' }
  ]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMsg = { role: 'user', content: input.trim() };
    const newMessages = [...messages, userMsg];
    setMessages(newMessages);
    setInput('');
    setError(null);
    setIsLoading(true);

    try {
      const res = await axios.post('http://localhost:3000/chat', { messages: newMessages });
      const botMsg = res.data.message;
      setMessages(prev => [...prev, botMsg]);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to send message. Please try again.');
      console.error(err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-4 min-h-screen flex flex-col">
      <h1 className="text-3xl font-bold mb-6 text-center text-gray-800">Student Assist Bot</h1>

      <div className="flex-grow border rounded-lg p-4 mb-4 overflow-y-auto bg-gray-50 min-h-[500px]">
        {messages.map((msg, i) => (
          <div key={i} className={`${msg.role === 'user' ? 'text-right' : 'text-left'} my-2`}>
            <span className={`inline-block px-4 py-2 rounded-lg ${
              msg.role === 'user' 
                ? 'bg-blue-500 text-white' 
                : 'bg-gray-200 text-gray-800'
            }`}>
              {msg.content}
            </span>
          </div>
        ))}
        {isLoading && (
          <div className="text-left my-2">
            <span className="inline-block px-4 py-2 rounded-lg bg-gray-200">
              <div className="typing-indicator space-x-1">
                <span className="dot animate-bounce">.</span>
                <span className="dot animate-bounce delay-100">.</span>
                <span className="dot animate-bounce delay-200">.</span>
              </div>
            </span>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>

      {error && (
        <div className="mb-4 p-3 rounded bg-red-100 text-red-700 flex items-center">
          <ExclamationCircleIcon className="h-5 w-5 mr-2" />
          {error}
        </div>
      )}

      <div className="flex items-center gap-2">
        <textarea
          className="flex-grow p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Type your question... (Press Enter to send)"
          rows="2"
          disabled={isLoading}
        />
        <button
          onClick={sendMessage}
          disabled={isLoading || !input.trim()}
          className={`px-6 py-3 rounded-lg font-medium ${
            isLoading || !input.trim()
              ? 'bg-gray-300 cursor-not-allowed'
              : 'bg-blue-500 hover:bg-blue-600 text-white'
          }`}
        >
          Send
        </button>
      </div>
    </div>
  );
}

export default App;
