import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { ExclamationCircleIcon } from '@heroicons/react/24/outline';

function App() {
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([
    { role: 'user', content: 'Hello!' }
  ]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
    // Send the initial message
    if (messages.length === 1 && messages[0].role === 'user') {
      sendMessage();
    }
  }, []);

  const sendMessage = async () => {
    if (!input.trim() && messages.length > 1) return;

    const userMsg = { role: 'user', content: input.trim() || messages[0].content };
    const newMessages = [...messages, userMsg];
    setMessages(newMessages);
    setInput('');
    setError(null);
    setIsLoading(true);

    try {
      console.log('Sending request to backend:', { messages: newMessages });
      const res = await axios.post('http://localhost:5000/chat', { messages: newMessages });
      console.log('Received response from backend:', res.data);
      const botMsg = res.data.message;
      setMessages(prev => [...prev, botMsg]);
    } catch (err) {
      console.error('Error in API call:', err.response?.data || err);
      const errorDetails = err.response?.data || {};
      setError(
        `Error: ${errorDetails.error || 'Failed to send message'}\n` +
        `Details: ${errorDetails.details || 'Please try again'}\n` +
        (errorDetails.code ? `Code: ${errorDetails.code}\n` : '') +
        (errorDetails.type ? `Type: ${errorDetails.type}` : '')
      );
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div className="app-container">
      <h1 className="app-title">Student Assist Bot</h1>

      <div className="chat-container">
        {messages.map((msg, i) => (
          <div key={i} className={`message ${msg.role}`}>
            <span className="message-content">
              {msg.content}
            </span>
          </div>
        ))}
        {isLoading && (
          <div className="message assistant">
            <div className="typing-indicator">
              <span className="dot"></span>
              <span className="dot"></span>
              <span className="dot"></span>
            </div>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>

      {error && (
        <div className="error-message">
          <ExclamationCircleIcon />
          <pre style={{ whiteSpace: 'pre-wrap', margin: 0 }}>{error}</pre>
        </div>
      )}

      <div className="input-container">
        <textarea
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Type your question... (Press Enter to send)"
          rows="2"
          disabled={isLoading}
        />
        <button
          onClick={sendMessage}
          disabled={isLoading || (!input.trim() && messages.length > 1)}
        >
          Send
        </button>
      </div>
    </div>
  );
}

export default App;
